import { useEffect, useState } from "react";
import MarioCharacter from "../../../assets/img/mario.png";
import "./LoadingScreen.css";
import { setLoadingScreen } from "../../../config/redux/engineSlice";
import { useDispatch, useSelector } from "react-redux";

const LoadingScreen = () => {
 const [isReady, setIsReady] = useState(false);
 const dispatch = useDispatch();
 const isLoggedIn = useSelector(state => state.auth.isLoggedIn); // Assuming you have an isLoggedIn state in your Redux store

 useEffect(() => {
    setTimeout(() => {
      setIsReady(true);
    }, 5000);
 }, []);

 useEffect(() => {
    if (isLoggedIn) {
      dispatch(setLoadingScreen(false));
    }
 }, [isLoggedIn, dispatch]);

 return (
    <div className="loading-screen-container">
      <img src={MarioCharacter} alt="" className="loading-mario" />
      {!isReady && <h1 className="loading-title">Loading...</h1>}
      {isReady && !isLoggedIn && (
        <button
          className="enter-button"
          onClick={() => dispatch(setLoadingScreen(false))}
        >
          ENTER
        </button>
      )}
    </div>
 );
};
export default LoadingScreen;
